import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { loadFromLocalStorage, saveToLocalStorage } from '../utils/dataStorage';

const DashboardContainer = styled.div`
  padding: 20px;
  color: white;
  min-height: 100vh;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  flex-wrap: wrap;
`;

const Title = styled.h1`
  margin: 0;
  color: gold;
`;

const LogoutButton = styled.button`
  padding: 10px 20px;
  background: #ff4757;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;

  &:hover {
    background: #ff6b81;
  }
`;

const RoleSwitcher = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
  flex-wrap: wrap;
`;

const RoleButton = styled.button`
  padding: 8px 16px;
  background: ${props => props.active ? 'gold' : 'rgba(255,255,255,0.2)'};
  color: ${props => props.active ? '#333' : 'white'};
  border: none;
  border-radius: 20px;
  cursor: pointer;
  font-weight: bold;
  transition: all 0.3s ease;

  &:hover {
    background: ${props => props.active ? 'gold' : 'rgba(255,255,255,0.3)'};
  }
`;

const FormContainer = styled.div`
  background: rgba(255,255,255,0.1);
  border-radius: 15px;
  padding: 30px;
  backdrop-filter: blur(10px);
  margin-bottom: 30px;
`;

const FormTitle = styled.h2`
  margin-top: 0;
  color: gold;
  border-bottom: 1px solid rgba(255,255,255,0.2);
  padding-bottom: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  font-weight: bold;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  font-size: 1rem;

  &:focus {
    outline: 2px solid gold;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  font-size: 1rem;

  &:focus {
    outline: 2px solid gold;
  }
`;

const Button = styled.button`
  padding: 12px 25px;
  background: gold;
  color: #333;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;
  font-size: 1rem;
  transition: all 0.3s ease;
  margin-right: 10px;
  margin-bottom: 10px;

  &:hover {
    background: #ffd700;
    transform: translateY(-2px);
  }
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: rgba(255,255,255,0.1);
  border-radius: 10px;
  overflow: hidden;
`;

const Th = styled.th`
  background: rgba(0,0,0,0.2);
  padding: 15px;
  text-align: left;
`;

const Td = styled.td`
  padding: 15px;
  border-bottom: 1px solid rgba(255,255,255,0.1);
`;

const Tr = styled.tr`
  &:hover {
    background: rgba(255,255,255,0.05);
  }
`;

const TeamManagerDashboard = () => {
  const navigate = useNavigate();
  const [students, setStudents] = useState([]);
  const [categories, setCategories] = useState([]);
  const [teams, setTeams] = useState([]);
  const [managedTeam, setManagedTeam] = useState('');
  const [teamManagerUsername, setTeamManagerUsername] = useState('');
  const [selectedTeam, setSelectedTeam] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [editingStudent, setEditingStudent] = useState(null);
  const [newStudent, setNewStudent] = useState({
    name: '',
    chestNumber: '',
    category: '',
    team: ''
  });

  // Load data from localStorage
  useEffect(() => {
    const savedStudents = loadFromLocalStorage('students', []);
    const savedCategories = loadFromLocalStorage('categories', []);
    const savedTeams = loadFromLocalStorage('teams', []);
    const savedManagedTeam = loadFromLocalStorage('managedTeam', '');
    const savedTeamManagerUsername = loadFromLocalStorage('teamManagerUsername', '');
    
    setStudents(savedStudents);
    setCategories(savedCategories);
    setTeams(savedTeams);
    setManagedTeam(savedManagedTeam);
    setTeamManagerUsername(savedTeamManagerUsername);
    
    // Set the managed team as the default team
    if (savedManagedTeam) {
      setSelectedTeam(savedManagedTeam);
      setNewStudent(prev => ({ ...prev, team: savedManagedTeam }));
    } else if (savedTeams.length > 0) {
      const defaultTeam = savedTeams[0].name;
      setSelectedTeam(defaultTeam);
      setNewStudent(prev => ({ ...prev, team: defaultTeam }));
    }
  }, []);

  // Filter students by selected team and search term
  const filteredStudents = students.filter(student => {
    // If user is managing a specific team, only show students from that team
    // Otherwise, show students based on the selectedTeam filter
    const teamFilter = managedTeam ? managedTeam : selectedTeam;
    const teamMatch = !teamFilter || student.team === teamFilter;
    const searchMatch = !searchTerm || 
      student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      student.chestNumber.toLowerCase().includes(searchTerm.toLowerCase());
    return teamMatch && searchMatch;
  });

  const handleLogout = () => {
    // Clear authentication state
    localStorage.removeItem('isTeamManagerLoggedIn');
    localStorage.removeItem('managedTeam');
    navigate('/team-manager-login');
  };

  const handleRegisterStudent = () => {
    if (newStudent.name.trim() === '' || newStudent.chestNumber.trim() === '') return;
    
    // Ensure the student is assigned to the managed team
    const studentData = managedTeam 
      ? { ...newStudent, team: managedTeam } 
      : newStudent;
    
    if (editingStudent) {
      // Update existing student
      const updatedStudents = students.map(student => 
        student.id === editingStudent.id 
          ? { ...student, ...studentData }
          : student
      );
      setStudents(updatedStudents);
      saveToLocalStorage('students', updatedStudents);
      setEditingStudent(null);
    } else {
      // Add new student
      const student = {
        id: students.length > 0 ? Math.max(...students.map(s => s.id)) + 1 : 1,
        name: studentData.name,
        chestNumber: studentData.chestNumber,
        category: studentData.category,
        team: studentData.team,
        photo: null
      };
      
      const updatedStudents = [...students, student];
      setStudents(updatedStudents);
      saveToLocalStorage('students', updatedStudents);
    }
    
    // Reset form
    setNewStudent({ 
      name: '', 
      chestNumber: '', 
      category: newStudent.category, 
      team: managedTeam || newStudent.team 
    });
  };

  const handleEditStudent = (student) => {
    // Only allow editing students from the managed team
    if (managedTeam && student.team !== managedTeam) {
      alert('You can only edit students from your managed team.');
      return;
    }
    
    setEditingStudent(student);
    setNewStudent({
      name: student.name,
      chestNumber: student.chestNumber,
      category: student.category,
      team: student.team
    });
  };

  const handleDeleteStudent = (studentId) => {
    // Check if student belongs to managed team
    const student = students.find(s => s.id === studentId);
    if (managedTeam && student.team !== managedTeam) {
      alert('You can only delete students from your managed team.');
      return;
    }
    
    if (window.confirm('Are you sure you want to delete this student?')) {
      const updatedStudents = students.filter(student => student.id !== studentId);
      setStudents(updatedStudents);
      saveToLocalStorage('students', updatedStudents);
      
      // Clear form if we were editing this student
      if (editingStudent && editingStudent.id === studentId) {
        setEditingStudent(null);
        setNewStudent({ 
          name: '', 
          chestNumber: '', 
          category: newStudent.category, 
          team: managedTeam || newStudent.team 
        });
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingStudent(null);
    setNewStudent({ 
      name: '', 
      chestNumber: '', 
      category: newStudent.category, 
      team: managedTeam || newStudent.team 
    });
  };

  return (
    <DashboardContainer>
      <Header>
        <div>
          <Title>Team Manager Dashboard</Title>
          {teamManagerUsername && (
            <p style={{ margin: '5px 0 0 0', color: 'rgba(255,255,255,0.8)' }}>
              Logged in as: <strong>{teamManagerUsername}</strong>
            </p>
          )}
        </div>
        <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
      </Header>
      
      <RoleSwitcher>
        <RoleButton 
          active={false}
          onClick={() => navigate('/')}
        >
          Public View
        </RoleButton>
        <RoleButton 
          active={false}
          onClick={() => navigate('/admin/login')}
        >
          Admin Dashboard
        </RoleButton>
        <RoleButton 
          active={true}
          onClick={() => {}}
        >
          Team Manager
        </RoleButton>
      </RoleSwitcher>
      
      {managedTeam && (
        <div style={{ 
          background: 'rgba(255,215,0,0.2)', 
          padding: '15px', 
          borderRadius: '10px', 
          marginBottom: '20px',
          textAlign: 'center'
        }}>
          <p style={{ margin: 0, color: 'gold' }}>
            <strong>Managing Team:</strong> {managedTeam}
          </p>
        </div>
      )}
      
      <FormContainer>
        <FormTitle>{editingStudent ? 'Edit Student' : 'Register Student'}</FormTitle>
        <FormGroup>
          <Label>Student Name</Label>
          <Input 
            type="text" 
            value={newStudent.name}
            onChange={(e) => setNewStudent({...newStudent, name: e.target.value})}
            placeholder="Enter student name"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>Chest Number</Label>
          <Input 
            type="text" 
            value={newStudent.chestNumber}
            onChange={(e) => setNewStudent({...newStudent, chestNumber: e.target.value})}
            placeholder="Enter chest number"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>Team</Label>
          {managedTeam ? (
            <Input 
              type="text" 
              value={managedTeam}
              disabled
              style={{ background: 'rgba(255,255,255,0.05)' }}
            />
          ) : (
            <Select 
              value={newStudent.team}
              onChange={(e) => setNewStudent({...newStudent, team: e.target.value})}
            >
              <option value="">Select a team</option>
              {teams.map(team => (
                <option key={team.id} value={team.name}>{team.name}</option>
              ))}
            </Select>
          )}
        </FormGroup>
        
        <FormGroup>
          <Label>Category</Label>
          <Select 
            value={newStudent.category}
            onChange={(e) => setNewStudent({...newStudent, category: e.target.value})}
          >
            <option value="">Select a category</option>
            {categories.map(category => (
              <option key={category.id} value={category.name}>{category.name}</option>
            ))}
          </Select>
        </FormGroup>
        
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
          <Button onClick={handleRegisterStudent}>
            {editingStudent ? 'Update Student' : 'Register Student'}
          </Button>
          {editingStudent && (
            <Button 
              onClick={handleCancelEdit}
              style={{ background: '#ff4757' }}
            >
              Cancel
            </Button>
          )}
        </div>
      </FormContainer>
      
      <FormContainer>
        <FormTitle>Manage Students</FormTitle>
        
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px', flexWrap: 'wrap' }}>
          {!managedTeam && (
            <div style={{ flex: 1, minWidth: '200px' }}>
              <Label>Filter by Team</Label>
              <Select 
                value={selectedTeam}
                onChange={(e) => setSelectedTeam(e.target.value)}
              >
                <option value="">All Teams</option>
                {teams.map(team => (
                  <option key={team.id} value={team.name}>{team.name}</option>
                ))}
              </Select>
            </div>
          )}
          
          <div style={{ flex: 1, minWidth: '200px' }}>
            <Label>Search Students</Label>
            <Input 
              type="text" 
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search by name or chest number"
            />
          </div>
        </div>
        
        {filteredStudents.length === 0 ? (
          <p>No students found.</p>
        ) : (
          <Table>
            <thead>
              <tr>
                <Th>Name</Th>
                <Th>Chest Number</Th>
                <Th>Team</Th>
                <Th>Category</Th>
                <Th>Actions</Th>
              </tr>
            </thead>
            <tbody>
              {filteredStudents.map(student => (
                <Tr key={student.id}>
                  <Td>{student.name}</Td>
                  <Td>{student.chestNumber}</Td>
                  <Td>{student.team}</Td>
                  <Td>{student.category}</Td>
                  <Td>
                    <Button 
                      onClick={() => handleEditStudent(student)}
                      style={{ padding: '5px 10px', marginRight: '5px' }}
                    >
                      Edit
                    </Button>
                    <Button 
                      onClick={() => handleDeleteStudent(student.id)}
                      style={{ padding: '5px 10px', background: '#ff4757' }}
                    >
                      Delete
                    </Button>
                  </Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        )}
      </FormContainer>
    </DashboardContainer>
  );
};

export default TeamManagerDashboard;
