import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { loadFromLocalStorage, saveToLocalStorage } from '../utils/dataStorage';

const DashboardContainer = styled.div`
  padding: 20px;
  color: white;
  min-height: 100vh;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  flex-wrap: wrap;
`;

const Title = styled.h1`
  margin: 0;
  color: gold;
`;

const LogoutButton = styled.button`
  padding: 10px 20px;
  background: #ff4757;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;

  &:hover {
    background: #ff6b81;
  }
`;

const FormContainer = styled.div`
  background: rgba(255,255,255,0.1);
  border-radius: 15px;
  padding: 30px;
  backdrop-filter: blur(10px);
  margin-bottom: 30px;
`;

const FormTitle = styled.h2`
  margin-top: 0;
  color: gold;
  border-bottom: 1px solid rgba(255,255,255,0.2);
  padding-bottom: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  font-weight: bold;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  font-size: 1rem;

  &:focus {
    outline: 2px solid gold;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  font-size: 1rem;

  &:focus {
    outline: 2px solid gold;
  }
`;

const Button = styled.button`
  padding: 12px 25px;
  background: gold;
  color: #333;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;
  font-size: 1rem;
  transition: all 0.3s ease;
  margin-right: 10px;
  margin-bottom: 10px;

  &:hover {
    background: #ffd700;
    transform: translateY(-2px);
  }
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: rgba(255,255,255,0.1);
  border-radius: 10px;
  overflow: hidden;
`;

const Th = styled.th`
  background: rgba(0,0,0,0.2);
  padding: 15px;
  text-align: left;
`;

const Td = styled.td`
  padding: 15px;
  border-bottom: 1px solid rgba(255,255,255,0.1);
`;

const Tr = styled.tr`
  &:hover {
    background: rgba(255,255,255,0.05);
  }
`;

const TeamManagerDashboard = () => {
  const navigate = useNavigate();
  const [students, setStudents] = useState([]);
  const [categories, setCategories] = useState([]);
  const [teams, setTeams] = useState([]);
  const [newStudent, setNewStudent] = useState({
    name: '',
    chestNumber: '',
    category: '',
    team: ''
  });

  // Load data from localStorage
  useEffect(() => {
    const savedStudents = loadFromLocalStorage('students', []);
    const savedCategories = loadFromLocalStorage('categories', []);
    const savedTeams = loadFromLocalStorage('teams', []);
    
    setStudents(savedStudents);
    setCategories(savedCategories);
    setTeams(savedTeams);
    
    // Set default team if available
    if (savedTeams.length > 0) {
      setNewStudent(prev => ({ ...prev, team: savedTeams[0].name }));
    }
  }, []);

  const handleLogout = () => {
    // Clear authentication state
    localStorage.removeItem('isTeamManagerLoggedIn');
    navigate('/team-manager-login');
  };

  const handleRegisterStudent = () => {
    if (newStudent.name.trim() === '' || newStudent.chestNumber.trim() === '') return;
    
    const student = {
      id: students.length + 1,
      name: newStudent.name,
      chestNumber: newStudent.chestNumber,
      category: newStudent.category,
      team: newStudent.team,
      photo: null
    };
    
    const updatedStudents = [...students, student];
    setStudents(updatedStudents);
    saveToLocalStorage('students', updatedStudents);
    
    setNewStudent({ 
      name: '', 
      chestNumber: '', 
      category: newStudent.category, 
      team: newStudent.team 
    });
  };

  return (
    <DashboardContainer>
      <Header>
        <Title>Team Manager Dashboard</Title>
        <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
      </Header>
      
      <FormContainer>
        <FormTitle>Register Student</FormTitle>
        <FormGroup>
          <Label>Student Name</Label>
          <Input 
            type="text" 
            value={newStudent.name}
            onChange={(e) => setNewStudent({...newStudent, name: e.target.value})}
            placeholder="Enter student name"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>Chest Number</Label>
          <Input 
            type="text" 
            value={newStudent.chestNumber}
            onChange={(e) => setNewStudent({...newStudent, chestNumber: e.target.value})}
            placeholder="Enter chest number"
          />
        </FormGroup>
        
        <FormGroup>
          <Label>Team</Label>
          <Select 
            value={newStudent.team}
            onChange={(e) => setNewStudent({...newStudent, team: e.target.value})}
          >
            <option value="">Select a team</option>
            {teams.map(team => (
              <option key={team.id} value={team.name}>{team.name}</option>
            ))}
          </Select>
        </FormGroup>
        
        <FormGroup>
          <Label>Category</Label>
          <Select 
            value={newStudent.category}
            onChange={(e) => setNewStudent({...newStudent, category: e.target.value})}
          >
            <option value="">Select a category</option>
            {categories.map(category => (
              <option key={category.id} value={category.name}>{category.name}</option>
            ))}
          </Select>
        </FormGroup>
        
        <Button onClick={handleRegisterStudent}>Register Student</Button>
      </FormContainer>
      
      <FormContainer>
        <FormTitle>Registered Students</FormTitle>
        {students.length === 0 ? (
          <p>No students registered yet.</p>
        ) : (
          <Table>
            <thead>
              <tr>
                <Th>Name</Th>
                <Th>Chest Number</Th>
                <Th>Team</Th>
                <Th>Category</Th>
              </tr>
            </thead>
            <tbody>
              {students.map(student => (
                <Tr key={student.id}>
                  <Td>{student.name}</Td>
                  <Td>{student.chestNumber}</Td>
                  <Td>{student.team}</Td>
                  <Td>{student.category}</Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        )}
      </FormContainer>
    </DashboardContainer>
  );
};

export default TeamManagerDashboard;
