import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { loadFromLocalStorage, saveToLocalStorage, calculateTeamStandings } from '../utils/dataStorage';

const DashboardContainer = styled.div`
  padding: 20px;
  color: white;
  min-height: 100vh;
`;

const Header = styled.h1`
  text-align: center;
  font-size: 2rem;
  margin-bottom: 30px;
  color: gold;
`;

const TabContainer = styled.div`
  display: flex;
  border-bottom: 2px solid rgba(255,255,255,0.2);
  margin-bottom: 30px;
  flex-wrap: wrap;
`;

const Tab = styled.button`
  padding: 15px 30px;
  background: ${props => props.active ? 'rgba(255,255,255,0.2)' : 'transparent'};
  color: ${props => props.active ? 'gold' : 'white'};
  border: none;
  cursor: pointer;
  font-size: 1.1rem;
  font-weight: bold;

  &:hover {
    background: rgba(255,255,255,0.1);
  }
  
  @media (max-width: 768px) {
    padding: 10px 15px;
    font-size: 0.9rem;
  }
`;

const FormContainer = styled.div`
  background: rgba(255,255,255,0.1);
  border-radius: 15px;
  padding: 30px;
  backdrop-filter: blur(10px);
  margin-bottom: 30px;
`;

const FormTitle = styled.h2`
  margin-top: 0;
  color: gold;
  border-bottom: 1px solid rgba(255,255,255,0.2);
  padding-bottom: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  font-weight: bold;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  font-size: 1rem;

  &:focus {
    outline: 2px solid gold;
  }
`;

const Select = styled.select`
  width: 100%;
  padding: 12px;
  border-radius: 8px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  font-size: 1rem;

  &:focus {
    outline: 2px solid gold;
  }
`;

const Button = styled.button`
  padding: 12px 25px;
  background: gold;
  color: #333;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: bold;
  font-size: 1rem;
  transition: all 0.3s ease;
  margin-right: 10px;
  margin-bottom: 10px;

  &:hover {
    background: #ffd700;
    transform: translateY(-2px);
  }
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  background: rgba(255,255,255,0.1);
  border-radius: 10px;
  overflow: hidden;
`;

const Th = styled.th`
  background: rgba(0,0,0,0.2);
  padding: 15px;
  text-align: left;
`;

const Td = styled.td`
  padding: 15px;
  border-bottom: 1px solid rgba(255,255,255,0.1);
`;

const Tr = styled.tr`
  &:hover {
    background: rgba(255,255,255,0.05);
  }
`;

const ColorPreview = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background: ${props => `rgb(${props.r}, ${props.g}, ${props.b})`};
  display: inline-block;
  margin-right: 10px;
`;

const RGBInputContainer = styled.div`
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
`;

const RGBInput = styled.input`
  width: 80px;
  padding: 8px;
  border-radius: 5px;
  border: none;
  background: rgba(255,255,255,0.1);
  color: white;
  text-align: center;
`;

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('events');
  const [events, setEvents] = useState([]);
  const [teams, setTeams] = useState([]);
  const [categories, setCategories] = useState([]);
  const [students, setStudents] = useState([]);
  const [programs, setPrograms] = useState([]);
  const [results, setResults] = useState([]);
  const [points, setPoints] = useState({
    gold: 10,
    silver: 7,
    bronze: 5
  });
  // Category-specific points configuration
  const [categoryPoints, setCategoryPoints] = useState({});
  // Grade-specific points configuration
  const [gradePoints, setGradePoints] = useState({});
  // Available grades configuration
  const [availableGrades, setAvailableGrades] = useState(['A', 'B', 'C', 'D']);
  // New grade points configuration
  const [newGradePoints, setNewGradePoints] = useState({
    gold: 10,
    silver: 7,
    bronze: 5
  });
  
  // Form states
  const [newEvent, setNewEvent] = useState({ name: '', categories: [], color: { r: 106, g: 17, b: 203 } });
  const [newTeam, setNewTeam] = useState({ name: '', color: '#FF5733' });
  const [newCategory, setNewCategory] = useState({ name: '', order: 1 });
  const [newStudent, setNewStudent] = useState({ name: '', chestNumber: '', team: '', category: '' });
  const [newProgram, setNewProgram] = useState({ name: '', type: 'Individual' });
  const [newResult, setNewResult] = useState({ 
    event: '', 
    program: '', 
    category: '', 
    participants: [{ name: '', position: '' }] 
  });
  const [adminPassword, setAdminPassword] = useState('admin123');
  const [selectedCategoryForPoints, setSelectedCategoryForPoints] = useState('');
  const [selectedGradeForPoints, setSelectedGradeForPoints] = useState('');
  const [newGrade, setNewGrade] = useState('');
  
  // Event filtering states
  const [selectedEventForTeams, setSelectedEventForTeams] = useState('');
  const [selectedEventForStudents, setSelectedEventForStudents] = useState('');
  const [selectedEventForPrograms, setSelectedEventForPrograms] = useState('');
  const [selectedEventForResults, setSelectedEventForResults] = useState('');

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem('isAdminLoggedIn');
    navigate('/');
  };

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedEvents = loadFromLocalStorage('events', []);
    const savedTeams = loadFromLocalStorage('teams', []);
    const savedCategories = loadFromLocalStorage('categories', []);
    const savedStudents = loadFromLocalStorage('students', []);
    const savedPrograms = loadFromLocalStorage('programs', []);
    const savedResults = loadFromLocalStorage('results', []);
    const savedPoints = loadFromLocalStorage('points', { gold: 10, silver: 7, bronze: 5 });
    const savedCategoryPoints = loadFromLocalStorage('categoryPoints', {});
    const savedGradePoints = loadFromLocalStorage('gradePoints', {});
    const savedPassword = loadFromLocalStorage('adminPassword', 'admin123');
    const savedAvailableGrades = loadFromLocalStorage('availableGrades', ['A', 'B', 'C', 'D']);
    
    // Load event filtering states
    const savedSelectedEventForTeams = loadFromLocalStorage('selectedEventForTeams', '');
    const savedSelectedEventForStudents = loadFromLocalStorage('selectedEventForStudents', '');
    const savedSelectedEventForPrograms = loadFromLocalStorage('selectedEventForPrograms', '');
    const savedSelectedEventForResults = loadFromLocalStorage('selectedEventForResults', '');
    
    setEvents(savedEvents);
    setTeams(savedTeams);
    setCategories(savedCategories);
    setStudents(savedStudents);
    setPrograms(savedPrograms);
    setResults(savedResults);
    setPoints(savedPoints);
    setCategoryPoints(savedCategoryPoints);
    setGradePoints(savedGradePoints);
    setAdminPassword(savedPassword);
    setAvailableGrades(savedAvailableGrades);
    
    // Set event filtering states
    setSelectedEventForTeams(savedSelectedEventForTeams);
    setSelectedEventForStudents(savedSelectedEventForStudents);
    setSelectedEventForPrograms(savedSelectedEventForPrograms);
    setSelectedEventForResults(savedSelectedEventForResults);
    
    // Initialize form select options
    if (savedEvents.length > 0) {
      setNewResult(prev => ({ ...prev, event: savedEvents[0].name }));
    }
    if (savedPrograms.length > 0) {
      setNewResult(prev => ({ ...prev, program: savedPrograms[0].name }));
    }
    if (savedCategories.length > 0) {
      setNewResult(prev => ({ ...prev, category: savedCategories[0].name }));
      setSelectedCategoryForPoints(savedCategories[0].name);
    }
  }, []);

  // Function to pre-configure points for a new grade across all categories
  const preConfigureGradePoints = (grade, pointsConfig) => {
    const updatedGradePoints = { ...gradePoints };
    
    // For each category, create a default grade point configuration
    categories.forEach(category => {
      const key = `${category.name}-${grade}`;
      updatedGradePoints[key] = { ...pointsConfig };
    });
    
    setGradePoints(updatedGradePoints);
  };

  // Update team standings when results or points change
  useEffect(() => {
    const savedTeams = loadFromLocalStorage('teams', []);
    const updatedTeams = calculateTeamStandings(savedTeams, results, points, categoryPoints, gradePoints);
    setTeams(updatedTeams);
  }, [results, points, categoryPoints, gradePoints]);

  // Save data to localStorage whenever state changes
  useEffect(() => {
    saveToLocalStorage('events', events);
  }, [events]);

  useEffect(() => {
    saveToLocalStorage('teams', teams);
  }, [teams]);

  useEffect(() => {
    saveToLocalStorage('categories', categories);
  }, [categories]);

  useEffect(() => {
    saveToLocalStorage('students', students);
  }, [students]);

  useEffect(() => {
    saveToLocalStorage('programs', programs);
  }, [programs]);

  useEffect(() => {
    saveToLocalStorage('results', results);
  }, [results]);

  useEffect(() => {
    saveToLocalStorage('points', points);
  }, [points]);

  useEffect(() => {
    saveToLocalStorage('categoryPoints', categoryPoints);
  }, [categoryPoints]);

  useEffect(() => {
    saveToLocalStorage('gradePoints', gradePoints);
  }, [gradePoints]);

  useEffect(() => {
    saveToLocalStorage('adminPassword', adminPassword);
  }, [adminPassword]);

  useEffect(() => {
    saveToLocalStorage('availableGrades', availableGrades);
  }, [availableGrades]);

  // Save event filtering states to localStorage
  useEffect(() => {
    saveToLocalStorage('selectedEventForTeams', selectedEventForTeams);
  }, [selectedEventForTeams]);

  useEffect(() => {
    saveToLocalStorage('selectedEventForStudents', selectedEventForStudents);
  }, [selectedEventForStudents]);

  useEffect(() => {
    saveToLocalStorage('selectedEventForPrograms', selectedEventForPrograms);
  }, [selectedEventForPrograms]);

  useEffect(() => {
    saveToLocalStorage('selectedEventForResults', selectedEventForResults);
  }, [selectedEventForResults]);

  const handleCreateEvent = () => {
    if (newEvent.name.trim() === '') return;
    const event = {
      id: events.length + 1,
      name: newEvent.name,
      categories: categories.map(cat => cat.name),
      color: newEvent.color
    };
    setEvents([...events, event]);
    setNewEvent({ name: '', categories: [], color: { r: 106, g: 17, b: 203 } });
  };

  const handleCreateTeam = () => {
    if (newTeam.name.trim() === '') return;
    const team = {
      id: teams.length + 1,
      name: newTeam.name,
      color: newTeam.color
    };
    setTeams([...teams, team]);
    setNewTeam({ name: '', color: '#FF5733' });
  };

  const handleCreateCategory = () => {
    if (newCategory.name.trim() === '') return;
    const category = {
      id: categories.length + 1,
      name: newCategory.name,
      order: newCategory.order
    };
    setCategories([...categories, category]);
    setNewCategory({ name: '', order: categories.length + 2 });
  };

  const handleCreateStudent = () => {
    if (newStudent.name.trim() === '' || newStudent.chestNumber.trim() === '') return;
    const student = {
      id: students.length + 1,
      name: newStudent.name,
      chestNumber: newStudent.chestNumber,
      team: newStudent.team,
      category: newStudent.category,
      photo: null
    };
    setStudents([...students, student]);
    setNewStudent({ name: '', chestNumber: '', team: '', category: '' });
  };

  const handleCreateProgram = () => {
    if (newProgram.name.trim() === '') return;
    const program = {
      id: programs.length + 1,
      name: newProgram.name,
      type: newProgram.type
    };
    setPrograms([...programs, program]);
    setNewProgram({ name: '', type: 'Individual' });
  };

  const handleAddParticipant = () => {
    setNewResult({
      ...newResult,
      participants: [...newResult.participants, { name: '', position: '' }]
    });
  };

  const handleParticipantChange = (index, field, value) => {
    const updatedParticipants = [...newResult.participants];
    updatedParticipants[index][field] = value;
    setNewResult({
      ...newResult,
      participants: updatedParticipants
    });
  };

  const handleRemoveParticipant = (index) => {
    if (newResult.participants.length <= 1) return;
    const updatedParticipants = [...newResult.participants];
    updatedParticipants.splice(index, 1);
    setNewResult({
      ...newResult,
      participants: updatedParticipants
    });
  };

  const handleSaveResult = () => {
    // Calculate points based on position, category, and grade
    const participantsWithPoints = newResult.participants.map(participant => {
      let pointValue = 0;
      
      // Extract grade from participant name if it exists (e.g., "John Doe (A)" or "SJ001 (B)")
      let grade = '';
      let participantName = participant.name;
      
      // Updated regex to handle more grades (A, B, C, D)
      const gradeMatch = participant.name.match(/\(([A-D])\)$/);
      if (gradeMatch) {
        grade = gradeMatch[1];
        participantName = participant.name.replace(/\s*\([A-D]\)$/, '');
      }
      
      // Use grade-specific points if available
      let pointConfig = points; // default
      
      // Use the category from the current result being saved, not the selected category for points configuration
      if (grade && newResult.category) {
        const gradeKey = `${newResult.category}-${grade}`;
        if (gradePoints[gradeKey]) {
          pointConfig = gradePoints[gradeKey];
        } else if (categoryPoints[newResult.category]) {
          // Fall back to category-specific points
          pointConfig = categoryPoints[newResult.category];
        }
      } else if (categoryPoints[newResult.category]) {
        // Use category-specific points if no grade specified
        pointConfig = categoryPoints[newResult.category];
      }
      
      switch (participant.position.toLowerCase()) {
        case '1':
        case 'first':
        case 'gold':
          pointValue = pointConfig.gold;
          break;
        case '2':
        case 'second':
        case 'silver':
          pointValue = pointConfig.silver;
          break;
        case '3':
        case 'third':
        case 'bronze':
          pointValue = pointConfig.bronze;
          break;
        default:
          pointValue = 0;
      }
      
      return {
        ...participant,
        points: pointValue
      };
    });
    
    const result = {
      id: results.length + 1,
      event: newResult.event,
      program: newResult.program,
      category: newResult.category,
      participants: participantsWithPoints,
      timestamp: new Date().toISOString()
    };
    
    setResults([...results, result]);
    
    // Reset form
    setNewResult({ 
      event: newResult.event, 
      program: newResult.program, 
      category: newResult.category, 
      participants: [{ name: '', position: '' }] 
    });
  };

  const handleChangePassword = () => {
    // In a real app, you would validate the current password first
    const newPassword = prompt('Enter new password:');
    if (newPassword && newPassword.trim() !== '') {
      setAdminPassword(newPassword.trim());
      alert('Password updated successfully!');
    }
  };

  const handleSaveCategoryPoints = () => {
    if (!selectedCategoryForPoints) return;
    
    setCategoryPoints({
      ...categoryPoints,
      [selectedCategoryForPoints]: { ...points }
    });
    
    alert(`Points configuration saved for category: ${selectedCategoryForPoints}`);
  };

  const handleCategoryChangeForPoints = (categoryName) => {
    setSelectedCategoryForPoints(categoryName);
    
    // Load existing points for this category if they exist
    if (categoryPoints[categoryName]) {
      setPoints(categoryPoints[categoryName]);
    } else {
      // Reset to default points if no custom points for this category
      setPoints({ gold: 10, silver: 7, bronze: 5 });
    }
  };

  // Handle grade selection for points configuration
  const handleGradeChangeForPoints = (grade) => {
    setSelectedGradeForPoints(grade);
    
    // Create a key for category-grade combination
    const key = `${selectedCategoryForPoints}-${grade}`;
    
    // Load existing points for this category-grade combination if they exist
    if (gradePoints[key]) {
      setPoints(gradePoints[key]);
    } else {
      // Check if there are category-specific points
      if (categoryPoints[selectedCategoryForPoints]) {
        setPoints(categoryPoints[selectedCategoryForPoints]);
      } else {
        // Reset to default points
        setPoints({ gold: 10, silver: 7, bronze: 5 });
      }
    }
  };

  // Pre-populate A grade points example
  const handlePrepopulateAGrade = () => {
    if (!selectedCategoryForPoints) {
      alert('Please select a category first');
      return;
    }
    
    setSelectedGradeForPoints('A');
    setPoints({ gold: 10, silver: 7, bronze: 5 });
  };

  // Save grade-specific points
  const handleSaveGradePoints = () => {
    if (!selectedCategoryForPoints || !selectedGradeForPoints) return;
    
    const key = `${selectedCategoryForPoints}-${selectedGradeForPoints}`;
    
    setGradePoints({
      ...gradePoints,
      [key]: { ...points }
    });
    
    alert(`Points configuration saved for category: ${selectedCategoryForPoints}, grade: ${selectedGradeForPoints}`);
  };

  return (
    <DashboardContainer>
      <Header>Admin Dashboard</Header>
      
      <TabContainer>
        <Tab active={activeTab === 'switcher'} onClick={() => setActiveTab('switcher')}>Role Switcher</Tab>
        <Tab active={activeTab === 'events'} onClick={() => setActiveTab('events')}>Events</Tab>
        <Tab active={activeTab === 'categories'} onClick={() => setActiveTab('categories')}>Categories</Tab>
        <Tab active={activeTab === 'teams'} onClick={() => setActiveTab('teams')}>Teams</Tab>
        <Tab active={activeTab === 'students'} onClick={() => setActiveTab('students')}>Students</Tab>
        <Tab active={activeTab === 'programs'} onClick={() => setActiveTab('programs')}>Programs</Tab>
        <Tab active={activeTab === 'results'} onClick={() => setActiveTab('results')}>Results</Tab>
        <Tab active={activeTab === 'grades'} onClick={() => setActiveTab('grades')}>Grades</Tab>
        <Tab active={activeTab === 'batch-points'} onClick={() => setActiveTab('batch-points')}>Batch Points</Tab>
        <Tab active={activeTab === 'points'} onClick={() => setActiveTab('points')}>Points</Tab>
      </TabContainer>
      
      {activeTab === 'switcher' && (
        <FormContainer>
          <FormTitle>Switch Between Roles</FormTitle>
          <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap', justifyContent: 'center' }}>
            <Button 
              onClick={() => navigate('/')}
              style={{ background: 'rgba(255,255,255,0.2)', color: 'white' }}
            >
              Public View
            </Button>
            <Button 
              onClick={() => {}}
              style={{ background: 'gold', color: '#333' }}
            >
              Admin Dashboard
            </Button>
            <Button 
              onClick={() => navigate('/team-manager-login')}
              style={{ background: 'rgba(255,255,255,0.2)', color: 'white' }}
            >
              Team Manager
            </Button>
            <Button 
              onClick={handleLogout}
              style={{ background: '#ff4757', color: 'white' }}
            >
              Logout
            </Button>
          </div>
        </FormContainer>
      )}
      
      {activeTab === 'events' && (
        <FormContainer>
          <FormTitle>Create New Event</FormTitle>
          <FormGroup>
            <Label>Event Name</Label>
            <Input 
              type="text" 
              value={newEvent.name}
              onChange={(e) => setNewEvent({...newEvent, name: e.target.value})}
              placeholder="Enter event name"
            />
          </FormGroup>
          
          <FormGroup>
            <Label>Event Color (RGB)</Label>
            <RGBInputContainer>
              <div>
                <Label>R</Label>
                <RGBInput 
                  type="number" 
                  min="0" 
                  max="255" 
                  value={newEvent.color.r}
                  onChange={(e) => setNewEvent({
                    ...newEvent, 
                    color: {...newEvent.color, r: parseInt(e.target.value) || 0}
                  })}
                />
              </div>
              <div>
                <Label>G</Label>
                <RGBInput 
                  type="number" 
                  min="0" 
                  max="255" 
                  value={newEvent.color.g}
                  onChange={(e) => setNewEvent({
                    ...newEvent, 
                    color: {...newEvent.color, g: parseInt(e.target.value) || 0}
                  })}
                />
              </div>
              <div>
                <Label>B</Label>
                <RGBInput 
                  type="number" 
                  min="0" 
                  max="255" 
                  value={newEvent.color.b}
                  onChange={(e) => setNewEvent({
                    ...newEvent, 
                    color: {...newEvent.color, b: parseInt(e.target.value) || 0}
                  })}
                />
              </div>
              <div>
                <Label>Preview</Label>
                <ColorPreview r={newEvent.color.r} g={newEvent.color.g} b={newEvent.color.b} />
              </div>
            </RGBInputContainer>
          </FormGroup>
          
          <Button onClick={handleCreateEvent}>Create Event</Button>
          
          <h3>Existing Events</h3>
          <Table>
            <thead>
              <tr>
                <Th>Name</Th>
                <Th>Categories</Th>
                <Th>Color</Th>
              </tr>
            </thead>
            <tbody>
              {events.map(event => (
                <Tr key={event.id}>
                  <Td>{event.name}</Td>
                  <Td>{event.categories.join(', ')}</Td>
                  <Td>
                    <ColorPreview r={event.color.r} g={event.color.g} b={event.color.b} />
                    RGB({event.color.r}, {event.color.g}, {event.color.b})
                  </Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        </FormContainer>
      )}
      
      {activeTab === 'categories' && (
        <FormContainer>
          <FormTitle>Manage Categories</FormTitle>
          <FormGroup>
            <Label>Category Name</Label>
            <Input 
              type="text" 
              value={newCategory.name}
              onChange={(e) => setNewCategory({...newCategory, name: e.target.value})}
              placeholder="Enter category name"
            />
          </FormGroup>
          
          <FormGroup>
            <Label>Display Order</Label>
            <Input 
              type="number" 
              value={newCategory.order}
              onChange={(e) => setNewCategory({...newCategory, order: parseInt(e.target.value) || 1})}
              placeholder="Enter display order"
            />
          </FormGroup>
          
          <Button onClick={handleCreateCategory}>Add Category</Button>
          
          <h3>Existing Categories</h3>
          <Table>
            <thead>
              <tr>
                <Th>Name</Th>
                <Th>Order</Th>
              </tr>
            </thead>
            <tbody>
              {categories.map(category => (
                <Tr key={category.id}>
                  <Td>{category.name}</Td>
                  <Td>{category.order}</Td>
                </Tr>
              ))}
            </tbody>
          </Table>
        </FormContainer>
      )}
      
      {activeTab === 'teams' && (
        <FormContainer>
          <FormTitle>Create New Team</FormTitle>
          <FormGroup>
            <Label>Team Name</Label>
            <Input 
              type="text" 
              value={newTeam.name}
              onChange={(e) => setNewTeam({...newTeam, name: e.target.value})}
              placeholder="Enter team name"
            />
          </FormGroup>
          
          <FormGroup>
            <Label>Team Color</Label>
            <Input 
              type="color" 
              value={newTeam.color}
              onChange={(e) => setNewTeam({...newTeam, color: e.target.value})}
            />
          </FormGroup>
          
          <Button onClick={handleCreateTeam}>Create Team</Button>
          
          {/* Event filter for teams */}
          <div style={{ marginTop: '30px' }}>
            <FormGroup>
              <Label>Filter Teams by Event</Label>
              <Select 
                value={selectedEventForTeams}
                onChange={(e) => setSelectedEventForTeams(e.target.value)}
              >
                <option value="">All Events</option>
                {events.map(event => (
                  <option key={event.id} value={event.name}>{event.name}</option>
                ))}
              </Select>
            </FormGroup>
            
            <h3>Existing Teams</h3>
            <Table>
              <thead>
                <tr>
                  <Th>Name</Th>
                  <Th>Color</Th>
                  {selectedEventForTeams && <Th>Participated in {selectedEventForTeams}</Th>}
                </tr>
              </thead>
              <tbody>
                {teams.map(team => {
                  // Filter teams that participated in the selected event if any
                  if (selectedEventForTeams) {
                    const teamResults = results.filter(result => 
                      result.event === selectedEventForTeams &&
                      result.participants.some(participant => {
                        const student = students.find(s => 
                          s.chestNumber === participant.name || 
                          s.name === participant.name
                        );
                        return student && student.team === team.name;
                      })
                    );
                    
                    // If no results for this team in the selected event, skip
                    if (teamResults.length === 0) return null;
                  }
                  
                  return (
                    <Tr key={team.id}>
                      <Td>{team.name}</Td>
                      <Td>
                        <ColorPreview r={parseInt(team.color.slice(1, 3), 16)} 
                                     g={parseInt(team.color.slice(3, 5), 16)} 
                                     b={parseInt(team.color.slice(5, 7), 16)} />
                        {team.color}
                      </Td>
                      {selectedEventForTeams && (
                        <Td>
                          Participated
                        </Td>
                      )}
                    </Tr>
                  );
                })}
              </tbody>
            </Table>
          </div>
        </FormContainer>
      )}
      
      {activeTab === 'students' && (
        <FormContainer>
          <FormTitle>Register Student</FormTitle>
          <FormGroup>
            <Label>Student Name</Label>
            <Input 
              type="text" 
              value={newStudent.name}
              onChange={(e) => setNewStudent({...newStudent, name: e.target.value})}
              placeholder="Enter student name"
            />
          </FormGroup>
          
          <FormGroup>
            <Label>Chest Number</Label>
            <Input 
              type="text" 
              value={newStudent.chestNumber}
              onChange={(e) => setNewStudent({...newStudent, chestNumber: e.target.value})}
              placeholder="Enter chest number"
            />
          </FormGroup>
          
          <FormGroup>
            <Label>Team</Label>
            <Select 
              value={newStudent.team}
              onChange={(e) => setNewStudent({...newStudent, team: e.target.value})}
            >
              <option value="">Select a team</option>
              {teams.map(team => (
                <option key={team.id} value={team.name}>{team.name}</option>
              ))}
            </Select>
          </FormGroup>
          
          <FormGroup>
            <Label>Category</Label>
            <Select 
              value={newStudent.category}
              onChange={(e) => setNewStudent({...newStudent, category: e.target.value})}
            >
              <option value="">Select a category</option>
              {categories.map(category => (
                <option key={category.id} value={category.name}>{category.name}</option>
              ))}
            </Select>
          </FormGroup>
          
          <Button onClick={handleCreateStudent}>Register Student</Button>
          
          {/* Event filter for students */}
          <div style={{ marginTop: '30px' }}>
            <FormGroup>
              <Label>Filter Students by Event</Label>
              <Select 
                value={selectedEventForStudents}
                onChange={(e) => setSelectedEventForStudents(e.target.value)}
              >
                <option value="">All Events</option>
                {events.map(event => (
                  <option key={event.id} value={event.name}>{event.name}</option>
                ))}
              </Select>
            </FormGroup>
            
            <h3>Registered Students</h3>
            <Table>
              <thead>
                <tr>
                  <Th>Name</Th>
                  <Th>Chest Number</Th>
                  <Th>Team</Th>
                  <Th>Category</Th>
                  {selectedEventForStudents && <Th>Participated in {selectedEventForStudents}</Th>}
                </tr>
              </thead>
              <tbody>
                {students.map(student => {
                  // Filter students who participated in the selected event if any
                  if (selectedEventForStudents) {
                    const studentResults = results.filter(result => 
                      result.event === selectedEventForStudents &&
                      result.participants.some(participant => 
                        participant.name === student.chestNumber || 
                        participant.name === student.name
                      )
                    );
                    
                    // If no results for this student in the selected event, skip
                    if (studentResults.length === 0) return null;
                  }
                  
                  return (
                    <Tr key={student.id}>
                      <Td>{student.name}</Td>
                      <Td>{student.chestNumber}</Td>
                      <Td>{student.team}</Td>
                      <Td>{student.category}</Td>
                      {selectedEventForStudents && (
                        <Td>
                          Participated
                        </Td>
                      )}
                    </Tr>
                  );
                })}
              </tbody>
            </Table>
          </div>
        </FormContainer>
      )}
      
      {activeTab === 'programs' && (
        <FormContainer>
          <FormTitle>Define Program</FormTitle>
          <FormGroup>
            <Label>Program Name</Label>
            <Input 
              type="text" 
              value={newProgram.name}
              onChange={(e) => setNewProgram({...newProgram, name: e.target.value})}
              placeholder="Enter program name"
            />
          </FormGroup>
          
          <FormGroup>
            <Label>Program Type</Label>
            <Select 
              value={newProgram.type}
              onChange={(e) => setNewProgram({...newProgram, type: e.target.value})}
            >
              <option value="Individual">Individual</option>
              <option value="Group">Group</option>
              <option value="General">General</option>
            </Select>
          </FormGroup>
          
          <Button onClick={handleCreateProgram}>Add Program</Button>
          
          {/* Event filter for programs */}
          <div style={{ marginTop: '30px' }}>
            <FormGroup>
              <Label>Filter Programs by Event</Label>
              <Select 
                value={selectedEventForPrograms}
                onChange={(e) => setSelectedEventForPrograms(e.target.value)}
              >
                <option value="">All Events</option>
                {events.map(event => (
                  <option key={event.id} value={event.name}>{event.name}</option>
                ))}
              </Select>
            </FormGroup>
            
            <h3>Existing Programs</h3>
            <Table>
              <thead>
                <tr>
                  <Th>Name</Th>
                  <Th>Type</Th>
                  {selectedEventForPrograms && <Th>Used in {selectedEventForPrograms}</Th>}
                </tr>
              </thead>
              <tbody>
                {programs.map(program => {
                  // Filter programs used in the selected event if any
                  if (selectedEventForPrograms) {
                    const programResults = results.filter(result => 
                      result.event === selectedEventForPrograms &&
                      result.program === program.name
                    );
                    
                    // If no results for this program in the selected event, skip
                    if (programResults.length === 0) return null;
                  }
                  
                  return (
                    <Tr key={program.id}>
                      <Td>{program.name}</Td>
                      <Td>{program.type}</Td>
                      {selectedEventForPrograms && (
                        <Td>
                          Used
                        </Td>
                      )}
                    </Tr>
                  );
                })}
              </tbody>
            </Table>
          </div>
        </FormContainer>
      )}
      
      {activeTab === 'results' && (
        <FormContainer>
          <FormTitle>Enter Results</FormTitle>
          
          <div style={{ 
            background: 'rgba(255,255,255,0.1)', 
            padding: '15px', 
            borderRadius: '10px', 
            marginBottom: '20px' 
          }}>
            <p style={{ margin: '0 0 10px 0', color: 'gold' }}>
              <strong>Tip:</strong> To use grade-specific points, include the grade in participant names
            </p>
            <p style={{ margin: '0 0 5px 0', fontSize: '0.9rem' }}>
              Examples: "John Doe (A)" or "SJ001 (B)"
            </p>
            <p style={{ margin: 0, fontSize: '0.9rem', fontStyle: 'italic' }}>
              Configure grade points in the "Points" tab
            </p>
          </div>
          
          <FormGroup>
            <Label>Event</Label>
            <Select 
              value={newResult.event}
              onChange={(e) => setNewResult({...newResult, event: e.target.value})}
            >
              <option value="">Select an event</option>
              {events.map(event => (
                <option key={event.id} value={event.name}>{event.name}</option>
              ))}
            </Select>
          </FormGroup>
          
          <FormGroup>
            <Label>Program</Label>
            <Select 
              value={newResult.program}
              onChange={(e) => setNewResult({...newResult, program: e.target.value})}
            >
              <option value="">Select a program</option>
              {programs.map(program => (
                <option key={program.id} value={program.name}>{program.name}</option>
              ))}
            </Select>
          </FormGroup>
          
          <FormGroup>
            <Label>Category</Label>
            <Select 
              value={newResult.category}
              onChange={(e) => setNewResult({...newResult, category: e.target.value})}
            >
              <option value="">Select a category</option>
              {categories.map(category => (
                <option key={category.id} value={category.name}>{category.name}</option>
              ))}
            </Select>
          </FormGroup>
          
          <h3>Participants & Positions</h3>
          {newResult.participants.map((participant, index) => (
            <FormGroup key={index}>
              <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                <Input 
                  type="text" 
                  placeholder="Participant name or chest number"
                  value={participant.name}
                  onChange={(e) => handleParticipantChange(index, 'name', e.target.value)}
                  style={{ flex: 1 }}
                />
                <Input 
                  type="text" 
                  placeholder="Position (1st, 2nd, 3rd or Gold, Silver, Bronze)"
                  value={participant.position}
                  onChange={(e) => handleParticipantChange(index, 'position', e.target.value)}
                  style={{ flex: 1 }}
                />
                {newResult.participants.length > 1 && (
                  <Button onClick={() => handleRemoveParticipant(index)} style={{ backgroundColor: '#ff4757' }}>
                    Remove
                  </Button>
                )}
              </div>
            </FormGroup>
          ))}
          
          <Button onClick={handleAddParticipant}>Add Participant</Button>
          <Button onClick={handleSaveResult}>Save Result</Button>
          
          {/* Event filter for results */}
          <div style={{ marginTop: '30px' }}>
            <FormGroup>
              <Label>Filter Results by Event</Label>
              <Select 
                value={selectedEventForResults}
                onChange={(e) => setSelectedEventForResults(e.target.value)}
              >
                <option value="">All Events</option>
                {events.map(event => (
                  <option key={event.id} value={event.name}>{event.name}</option>
                ))}
              </Select>
            </FormGroup>
            
            <h3>Existing Results</h3>
            <Table>
              <thead>
                <tr>
                  <Th>Event</Th>
                  <Th>Program</Th>
                  <Th>Category</Th>
                  <Th>Participants</Th>
                </tr>
              </thead>
              <tbody>
                {results
                  .filter(result => !selectedEventForResults || result.event === selectedEventForResults)
                  .map(result => (
                    <Tr key={result.id}>
                      <Td>{result.event}</Td>
                      <Td>{result.program}</Td>
                      <Td>{result.category}</Td>
                      <Td>
                        {result.participants.map((p, i) => (
                          <div key={i}>
                            {p.name} - {p.position} ({p.points} points)
                          </div>
                        ))}
                      </Td>
                    </Tr>
                  ))}
              </tbody>
            </Table>
          </div>
        </FormContainer>
      )}
      
      {activeTab === 'grades' && (
        <FormContainer>
          <FormTitle>Manage Grades</FormTitle>
          
          <div style={{ 
            background: 'rgba(255,255,255,0.1)', 
            padding: '15px', 
            borderRadius: '10px', 
            marginBottom: '20px' 
          }}>
            <h3 style={{ marginTop: 0, color: 'gold' }}>Add New Grade with Points</h3>
            <FormGroup>
              <Label>Grade Letter</Label>
              <Input 
                type="text" 
                value={newGrade}
                onChange={(e) => setNewGrade(e.target.value.toUpperCase())}
                placeholder="Enter grade (e.g., A, B, C)"
                maxLength="1"
                style={{ width: '80px' }}
              />
            </FormGroup>
            
            <FormGroup>
              <Label>Default Points for this Grade</Label>
              <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
                <div>
                  <Label>1st (Gold)</Label>
                  <Input 
                    type="number" 
                    value={newGradePoints.gold}
                    onChange={(e) => setNewGradePoints({...newGradePoints, gold: parseInt(e.target.value) || 0})}
                    style={{ width: '80px' }}
                  />
                </div>
                <div>
                  <Label>2nd (Silver)</Label>
                  <Input 
                    type="number" 
                    value={newGradePoints.silver}
                    onChange={(e) => setNewGradePoints({...newGradePoints, silver: parseInt(e.target.value) || 0})}
                    style={{ width: '80px' }}
                  />
                </div>
                <div>
                  <Label>3rd (Bronze)</Label>
                  <Input 
                    type="number" 
                    value={newGradePoints.bronze}
                    onChange={(e) => setNewGradePoints({...newGradePoints, bronze: parseInt(e.target.value) || 0})}
                    style={{ width: '80px' }}
                  />
                </div>
              </div>
            </FormGroup>
            
            <Button 
              onClick={() => {
                if (newGrade && !availableGrades.includes(newGrade)) {
                  // Add the new grade to available grades
                  setAvailableGrades([...availableGrades, newGrade]);
                  
                  // Ask user if they want to pre-configure points for all categories
                  const preConfigure = window.confirm(
                    `Grade ${newGrade} added successfully!\n\n` +
                    `Do you want to pre-configure default points (${newGradePoints.gold}/${newGradePoints.silver}/${newGradePoints.bronze}) ` +
                    `for this grade across all categories?\n\n` +
                    `You can always customize these later in the Points tab.`
                  );
                  
                  if (preConfigure) {
                    preConfigureGradePoints(newGrade, newGradePoints);
                    alert(`Points pre-configured for grade ${newGrade} across all categories!`);
                  }
                  
                  setNewGrade('');
                  setNewGradePoints({ gold: 10, silver: 7, bronze: 5 });
                } else if (availableGrades.includes(newGrade)) {
                  alert(`Grade ${newGrade} already exists!`);
                } else {
                  alert('Please enter a valid grade letter.');
                }
              }}
            >
              Add Grade
            </Button>
          </div>
          
          <h3>Available Grades</h3>
          <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap', marginBottom: '20px' }}>
            {availableGrades.map((grade, index) => (
              <div 
                key={index} 
                style={{ 
                  background: 'rgba(255,255,255,0.2)', 
                  padding: '10px 15px', 
                  borderRadius: '20px',
                  display: 'flex',
                  alignItems: 'center'
                }}
              >
                <span>{grade}</span>
                <button 
                  onClick={() => {
                    if (availableGrades.length > 1) {
                      setAvailableGrades(availableGrades.filter(g => g !== grade));
                    } else {
                      alert('You must have at least one grade!');
                    }
                  }}
                  style={{ 
                    background: 'transparent', 
                    border: 'none', 
                    color: 'red', 
                    cursor: 'pointer',
                    marginLeft: '10px',
                    fontWeight: 'bold'
                  }}
                >
                  ×
                </button>
              </div>
            ))}
          </div>
          
          <div>
            <h3>Grade Point Configuration Example</h3>
            <div style={{ 
              background: 'rgba(255,255,255,0.1)', 
              padding: '20px', 
              borderRadius: '10px', 
              marginBottom: '20px' 
            }}>
              <p style={{ color: 'gold', fontWeight: 'bold' }}>Example: Configure A Grade Points</p>
              <ol style={{ paddingLeft: '20px', color: 'rgba(255,255,255,0.9)' }}>
                <li>Go to the "Points" tab</li>
                <li>Select a category (e.g., "Senior")</li>
                <li>From the "Select Grade" dropdown, choose "A Grade"</li>
                <li>Set points for each position:
                  <ul style={{ paddingLeft: '20px', marginTop: '5px' }}>
                    <li>1st Position (Gold): 10 points</li>
                    <li>2nd Position (Silver): 7 points</li>
                    <li>3rd Position (Bronze): 5 points</li>
                  </ul>
                </li>
                <li>Click "Save Grade Points"</li>
              </ol>
              <p style={{ fontStyle: 'italic', color: 'rgba(255,255,255,0.7)' }}>
                Participants with names like "John Doe (A)" will now receive these points instead of the default values.
              </p>
            </div>
            
            <p style={{ color: 'rgba(255,255,255,0.7)' }}>
              Note: Grades are used to differentiate point values for participants in the same category. 
              For example, you can have different point values for A grade and B grade participants in the same category.
              To use grade-specific points, include the grade in parentheses at the end of participant names (e.g., "Participant Name (A)").
            </p>
          </div>
        </FormContainer>
      )}
      
      {activeTab === 'batch-points' && (
        <FormContainer>
          <FormTitle>Batch Grade Points Configuration</FormTitle>
          <p style={{ color: 'rgba(255,255,255,0.7)' }}>
            Configure points for a grade across all categories at once
          </p>
          
          <FormGroup>
            <Label>Select Grade</Label>
            <Select 
              value={selectedGradeForPoints}
              onChange={(e) => {
                setSelectedGradeForPoints(e.target.value);
                // Load existing points if they exist for any category
                const existingConfig = Object.keys(gradePoints).find(key => 
                  key.endsWith(`-${e.target.value}`)
                );
                if (existingConfig && gradePoints[existingConfig]) {
                  setPoints(gradePoints[existingConfig]);
                }
              }}
            >
              <option value="">Select a grade</option>
              {availableGrades.map((grade, index) => (
                <option key={index} value={grade}>{grade} Grade</option>
              ))}
            </Select>
          </FormGroup>
          
          {selectedGradeForPoints && (
            <div style={{ backgroundColor: 'rgba(255,255,255,0.1)', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
              <h3>Set Points for {selectedGradeForPoints} Grade (All Categories)</h3>
              
              <FormGroup>
                <Label>1st Position (Gold) Points</Label>
                <Input 
                  type="number" 
                  value={points.gold}
                  onChange={(e) => setPoints({...points, gold: parseInt(e.target.value) || 0})}
                />
              </FormGroup>
              
              <FormGroup>
                <Label>2nd Position (Silver) Points</Label>
                <Input 
                  type="number" 
                  value={points.silver}
                  onChange={(e) => setPoints({...points, silver: parseInt(e.target.value) || 0})}
                />
              </FormGroup>
              
              <FormGroup>
                <Label>3rd Position (Bronze) Points</Label>
                <Input 
                  type="number" 
                  value={points.bronze}
                  onChange={(e) => setPoints({...points, bronze: parseInt(e.target.value) || 0})}
                />
              </FormGroup>
              
              <Button 
                onClick={() => {
                  if (!selectedGradeForPoints) return;
                  
                  const updatedGradePoints = { ...gradePoints };
                  let updatedCount = 0;
                  
                  // Update points for this grade across all categories
                  categories.forEach(category => {
                    const key = `${category.name}-${selectedGradeForPoints}`;
                    updatedGradePoints[key] = { ...points };
                    updatedCount++;
                  });
                  
                  setGradePoints(updatedGradePoints);
                  alert(`Points updated for ${updatedCount} categories with ${selectedGradeForPoints} grade`);
                }}
              >
                Apply to All Categories
              </Button>
              
              <div style={{ marginTop: '20px' }}>
                <h4>Current Configurations</h4>
                <Table>
                  <thead>
                    <tr>
                      <Th>Category</Th>
                      <Th>Grade</Th>
                      <Th>1st (Gold)</Th>
                      <Th>2nd (Silver)</Th>
                      <Th>3rd (Bronze)</Th>
                    </tr>
                  </thead>
                  <tbody>
                    {categories.map((category, index) => {
                      const key = `${category.name}-${selectedGradeForPoints}`;
                      const config = gradePoints[key] || points;
                      return (
                        <Tr key={index}>
                          <Td>{category.name}</Td>
                          <Td>{selectedGradeForPoints}</Td>
                          <Td>{config.gold}</Td>
                          <Td>{config.silver}</Td>
                          <Td>{config.bronze}</Td>
                        </Tr>
                      );
                    })}
                  </tbody>
                </Table>
              </div>
            </div>
          )}
        </FormContainer>
      )}
      
      {activeTab === 'points' && (
        <FormContainer>
          <FormTitle>Configure Points</FormTitle>
          
          <div style={{ 
            background: 'rgba(255,255,255,0.1)', 
            padding: '15px', 
            borderRadius: '10px', 
            marginBottom: '20px' 
          }}>
            <p style={{ margin: '0 0 10px 0', color: 'gold' }}>
              <strong>Quick Setup Example:</strong> To configure A grade with 10/7/5 points:
            </p>
            <ol style={{ margin: '0 0 10px 20px', fontSize: '0.9rem' }}>
              <li>Select a category below</li>
              <li>Choose "A Grade" from the dropdown</li>
              <li>Set Gold=10, Silver=7, Bronze=5</li>
              <li>Click "Save Grade Points"</li>
            </ol>
            <p style={{ margin: 0, fontSize: '0.9rem', fontStyle: 'italic' }}>
              Participants named "John Doe (A)" will get these points
            </p>
          </div>
          
          <FormGroup>
            <Label>Configure Points By Category</Label>
            <Select 
              value={selectedCategoryForPoints}
              onChange={(e) => handleCategoryChangeForPoints(e.target.value)}
            >
              <option value="">Select a category</option>
              {categories.map(category => (
                <option key={category.id} value={category.name}>{category.name}</option>
              ))}
            </Select>
          </FormGroup>
          
          {selectedCategoryForPoints && (
            <div style={{ backgroundColor: 'rgba(255,255,255,0.1)', padding: '20px', borderRadius: '10px', marginBottom: '20px' }}>
              <h3>Points for {selectedCategoryForPoints}</h3>
              
              <FormGroup>
                <Label>Select Grade (Optional)</Label>
                <Select 
                  value={selectedGradeForPoints}
                  onChange={(e) => handleGradeChangeForPoints(e.target.value)}
                >
                  <option value="">No grade (category only)</option>
                  {availableGrades.map((grade, index) => (
                    <option key={index} value={grade}>{grade} Grade</option>
                  ))}
                </Select>
                <Button 
                  onClick={handlePrepopulateAGrade}
                  style={{ 
                    marginLeft: '10px', 
                    background: 'rgba(255,215,0,0.2)',
                    color: 'gold'
                  }}
                >
                  Example: A Grade (10/7/5)
                </Button>
              </FormGroup>
              
              {selectedGradeForPoints && (
                <h4>Points for {selectedCategoryForPoints} - {selectedGradeForPoints} Grade</h4>
              )}
              
              <FormGroup>
                <Label>1st Position (Gold) Points</Label>
                <Input 
                  type="number" 
                  value={points.gold}
                  onChange={(e) => setPoints({...points, gold: parseInt(e.target.value) || 0})}
                />
              </FormGroup>
              
              <FormGroup>
                <Label>2nd Position (Silver) Points</Label>
                <Input 
                  type="number" 
                  value={points.silver}
                  onChange={(e) => setPoints({...points, silver: parseInt(e.target.value) || 0})}
                />
              </FormGroup>
              
              <FormGroup>
                <Label>3rd Position (Bronze) Points</Label>
                <Input 
                  type="number" 
                  value={points.bronze}
                  onChange={(e) => setPoints({...points, bronze: parseInt(e.target.value) || 0})}
                />
              </FormGroup>
              
              {selectedGradeForPoints ? (
                <Button onClick={handleSaveGradePoints}>Save Grade Points</Button>
              ) : (
                <Button onClick={handleSaveCategoryPoints}>Save Category Points</Button>
              )}
              
              {!selectedGradeForPoints && categoryPoints[selectedCategoryForPoints] && (
                <div style={{ marginTop: '10px', color: 'gold' }}>
                  <strong>Current category configuration saved</strong>
                </div>
              )}
              
              {selectedGradeForPoints && gradePoints[`${selectedCategoryForPoints}-${selectedGradeForPoints}`] && (
                <div style={{ marginTop: '10px', color: 'gold' }}>
                  <strong>Current grade configuration saved</strong>
                </div>
              )}
            </div>
          )}
          
          <FormTitle>Default Points Configuration</FormTitle>
          <p>These points will be used when no category-specific or grade-specific points are configured.</p>
          
          <FormGroup>
            <Label>1st Position (Gold) Points</Label>
            <Input 
              type="number" 
              value={points.gold}
              onChange={(e) => setPoints({...points, gold: parseInt(e.target.value) || 0})}
            />
          </FormGroup>
          
          <FormGroup>
            <Label>2nd Position (Silver) Points</Label>
            <Input 
              type="number" 
              value={points.silver}
              onChange={(e) => setPoints({...points, silver: parseInt(e.target.value) || 0})}
            />
          </FormGroup>
          
          <FormGroup>
            <Label>3rd Position (Bronze) Points</Label>
            <Input 
              type="number" 
              value={points.bronze}
              onChange={(e) => setPoints({...points, bronze: parseInt(e.target.value) || 0})}
            />
          </FormGroup>
          
          <Button onClick={() => saveToLocalStorage('points', points)}>Save Default Points Configuration</Button>
          <Button onClick={handleChangePassword}>Change Admin Password</Button>
          
          {Object.keys(categoryPoints).length > 0 && (
            <div style={{ marginTop: '30px' }}>
              <h3>Category-Specific Points Configuration</h3>
              <Table>
                <thead>
                  <tr>
                    <Th>Category</Th>
                    <Th>1st (Gold)</Th>
                    <Th>2nd (Silver)</Th>
                    <Th>3rd (Bronze)</Th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(categoryPoints).map(([category, pointsConfig]) => (
                    <Tr key={category}>
                      <Td>{category}</Td>
                      <Td>{pointsConfig.gold}</Td>
                      <Td>{pointsConfig.silver}</Td>
                      <Td>{pointsConfig.bronze}</Td>
                    </Tr>
                  ))}
                </tbody>
              </Table>
            </div>
          )}
          
          {Object.keys(gradePoints).length > 0 && (
            <div style={{ marginTop: '30px' }}>
              <h3>Grade-Specific Points Configuration</h3>
              <Table>
                <thead>
                  <tr>
                    <Th>Category</Th>
                    <Th>Grade</Th>
                    <Th>1st (Gold)</Th>
                    <Th>2nd (Silver)</Th>
                    <Th>3rd (Bronze)</Th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(gradePoints).map(([key, pointsConfig]) => {
                    const [category, grade] = key.split('-');
                    return (
                      <Tr key={key}>
                        <Td>{category}</Td>
                        <Td>{grade}</Td>
                        <Td>{pointsConfig.gold}</Td>
                        <Td>{pointsConfig.silver}</Td>
                        <Td>{pointsConfig.bronze}</Td>
                      </Tr>
                    );
                  })}
                </tbody>
              </Table>
            </div>
          )}
        </FormContainer>
      )}
    </DashboardContainer>
  );
};

export default AdminDashboard;
