                }} 
              />
              {newTeam.teamLeader?.photo && (
                <div style={{ marginTop: '10px' }}>
                  <img src={newTeam.teamLeader.photo} alt="Team Leader Preview" style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '50%' }} />
                </div>
              )}
            </FormGroup>
            
            {/* Assistant Leader */}
            <FormGroup>
              <Label>Assistant Leader Name</Label>
