                  const file = e.target.files[0];
                  if (file) {
                    const reader = new FileReader();
                    reader.onload = (event) => {
                      setNewTeam({ 
                        ...newTeam, 
                        assistantLeader: { 
                          ...newTeam.assistantLeader, 
                          photo: event.target.result 
