                      });
                    };
                    reader.readAsDataURL(file);
                  }
                }} 
              />
              {newTeam.assistantLeader?.photo && (
                <div style={{ marginTop: '10px' }}>
                  <img src={newTeam.assistantLeader.photo} alt="Assistant Leader Preview" style={{ maxWidth: '100px', maxHeight: '100px', borderRadius: '50%' }} />
                </div>
