            </FormGroup>
            
            {/* Manager */}
            <FormGroup>
              <Label>Manager Name</Label>
              <Input 
                type="text" 
                value={newTeam.manager?.name || ''} 
                onChange={(e) => setNewTeam({ 
                  ...newTeam, 
                  manager: { 
                    ...newTeam.manager, 
                    name: e.target.value 
