          </FormContainer>
          <Table>
            <thead>
              <tr>
                <Th>Event</Th>
                <Th>Category</Th>
                <Th>Name</Th>
                <Th>Type</Th>
                <Th>Actions</Th>
              </tr>
            </thead>
            <tbody>
              {programs.map((program, index) => (
                <Tr key={index}>
